import React from 'react';

const experiences = [
  {
    title: 'Técnico de Servicio',
    company: 'Minera Fresnillo PLC - Unidad Saucito',
    period: '2024 - Actualidad',
    description: [
      'Instalación y puesta en marcha de sistemas de aire acondicionado en equipos mineros.',
      'Implementación de programas de mantenimiento preventivo para prolongar la vida útil de los equipos.',
      'Mantenimiento correctivo eficiente, minimizando tiempos de inactividad mediante diagnóstico rápido.',
      'Diagnóstico y análisis de fallas eléctricas en equipos como minisplits y equipos paquete.'
    ],
    current: true,
  },
  {
    title: 'Instrumentista Especialista',
    company: 'Minera Fresnillo PLC - Unidad San Julián (Contratista Grupo Cavi)',
    period: '2021 - 2023',
    description: [
      'Mantenimiento preventivo y calibración de instrumentos industriales (temperatura, presión, nivel, flujo, pH, ORP, etc.).',
      'Manejo avanzado de protocolos industriales: Fieldbus Foundation, Profibus y DeviceNet.',
      'Interpretación de diagramas eléctricos, neumáticos e hidráulicos para instalaciones precisas.',
      'Operación y monitoreo de equipos críticos como Molino de Bolas, Molino SAG Outotec, Filtros de Zinc y Plomo, entre otros.',
      'Experiencia con software especializado como TIA Portal, RSLogix 500, FactoryTalk View y Connected Components Workbench.'
    ],
    current: false,
  }
];

const PortfolioExperience = () => {
  return (
    <section id="experience" className="py-20 bg-blue-950">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-white">Experiencia Laboral</h2>
          <div className="w-20 h-1 bg-cyan-400 mx-auto mt-4"></div>
        </div>
        <div className="space-y-10">
          {experiences.map((exp, index) => (
            <div key={index} className="relative pl-8 sm:pl-32 group">
              {/* Periodo */}
              <div className="font-medium text-xl text-cyan-400 mb-1 sm:mb-0">
                {exp.period}
              </div>

              {/* Título y Empresa */}
              <div className="flex flex-col sm:flex-row items-start mb-3 group-last:before:hidden before:absolute before:left-2 sm:before:left-0 before:h-full before:px-px before:bg-blue-700 sm:before:ml-[6.5rem] before:self-start before:-translate-x-1/2 before:translate-y-3 after:absolute after:left-2 sm:after:left-0 after:w-2 after:h-2 after:bg-cyan-400 after:rounded-full sm:after:ml-[6.5rem] after:-translate-x-1/2 after:translate-y-1.5">
                <div className="font-bold text-xl text-white">{exp.title}</div>
                <div className="text-blue-300 ml-0 sm:ml-4">@ {exp.company}</div>
              </div>

              {/* Descripción detallada */}
              <ul className="text-blue-200 list-disc pl-5 space-y-2">
                {exp.description.map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default PortfolioExperience;
